last_floor = 0
first_char_to_basement = -1
for i, c in enumerate(directions):
    if (c == '('):
        last_floor += 1
    elif (c == ')'):
        last_floor -= 1
    if (last_floor == -1 and first_char_to_basement == -1):
        first_char_to_basement = i


